import React, { useEffect, useRef } from 'react';
import TaskItem from './TaskItem';
import '../styles/TaskList.css';

function TaskList({ tasks, onUpdate, onDelete, onToggle }) {
  const carouselRef = useRef(null);

  useEffect(() => {
    const carousel = carouselRef.current;
    if (!carousel) return;

    let scrollAmount = 0;
    const scrollStep = 1; // מהירות
    const scrollInterval = 20; // כל כמה מילי־שניות לזוז

    const interval = setInterval(() => {
      if (!carousel) return;

      scrollAmount += scrollStep;

      if (scrollAmount >= carousel.scrollWidth / 2) {
        // קפיצה לאתחול (חזרה להתחלה)
        scrollAmount = 0;
        carousel.scrollTo({ left: 0 });
      } else {
        carousel.scrollTo({ left: scrollAmount });
      }
    }, scrollInterval);

    return () => clearInterval(interval);
  }, [tasks]);

  if (!tasks || tasks.length === 0) {
    return <p>No tasks yet</p>;
  }

  // שכפול משימות כדי ליצור אפקט אינסופי
  const duplicatedTasks = [...tasks, ...tasks];

  return (
    <div className="carousel-container">
      <div className="carousel" ref={carouselRef}>
        {duplicatedTasks.map((task, index) => (
          <div className="carousel-item" key={task.id + '-' + index}>
            <TaskItem
              task={task}
              onUpdate={onUpdate}
              onDelete={onDelete}
              onToggle={onToggle}
            />
          </div>
        ))}
      </div>
    </div>
  );
}

export default TaskList;
