import React, { useState } from 'react';
import '../styles/TaskItem.css';

function TaskItem({ task, onUpdate, onDelete, onToggle }) {
  const [isEditing, setIsEditing] = useState(false);
  const [title, setTitle] = useState(task?.title || '');
  const [description, setDescription] = useState(task?.description || '');
  const [priority, setPriority] = useState(task?.priority || 'medium');

  const handleSave = () => {
    onUpdate(task.id, { title, description, priority });
    setIsEditing(false);
  };

  const priorityText = {
    high: 'גבוהה',
    medium: 'בינונית',
    low: 'נמוכה'
  };

  if (isEditing) {
    return (
      <div className="task-item editing">
        <div className="task-form-grid">
          <div className="form-group">
            <label className="form-label">כותרת</label>
            <input 
              className="form-input"
              value={title} 
              onChange={(e) => setTitle(e.target.value)} 
            />
          </div>
          <div className="form-group">
            <label className="form-label">תיאור</label>
            <input 
              className="form-input"
              value={description} 
              onChange={(e) => setDescription(e.target.value)} 
            />
          </div>
          <div className="form-group">
            <label className="form-label">עדיפות</label>
            <select className="form-select" value={priority} onChange={(e) => setPriority(e.target.value)}>
              <option value="low">נמוכה</option>
              <option value="medium">בינונית</option>
              <option value="high">גבוהה</option>
            </select>
          </div>
        </div>
        <div className="task-actions" style={{marginTop: '16px', justifyContent: 'flex-start', gap: '12px'}}>
          <button className="submit-btn" onClick={handleSave}>שמור</button>
          <button className="delete-btn" onClick={() => setIsEditing(false)}>בטל</button>
          <button className="delete-btn" onClick={() => onDelete(task.id)}>מחק</button>
          <button className="submit-btn" onClick={() => onToggle(task.id)}>
            {task.completed ? 'סמן כלא הושלם' : 'סמן כהושלם'}
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className={`task-item priority-${task.priority} ${task.completed ? 'completed' : ''}`}>
      <div className="task-header">
        <h3 className="task-title">{task.title}</h3>
        <div className="task-actions">
          <button className="delete-btn" onClick={() => onDelete(task.id)}>מחק</button>
          <input 
            type="checkbox" 
            className="task-checkbox"
            checked={task.completed}
            onChange={() => onToggle(task.id)}
          />
        </div>
      </div>

      {task.description && (
        <p className="task-description">{task.description}</p>
      )}

      <div className="task-meta">
        <span className={`priority-badge ${task.priority}`}>
          {priorityText[task.priority]}
        </span>
        <button 
          className="edit-btn"
          onClick={() => setIsEditing(true)}
          style={{
            background: 'none',
            border: '1px solid #d1d5db',
            borderRadius: '6px',
            padding: '4px 8px',
            fontSize: '0.75rem',
            color: '#6b7280',
            cursor: 'pointer',
            transition: 'all 0.3s ease'
          }}
          onMouseOver={(e) => {
            e.target.style.borderColor = '#6366f1';
            e.target.style.color = '#6366f1';
          }}
          onMouseOut={(e) => {
            e.target.style.borderColor = '#d1d5db';
            e.target.style.color = '#6b7280';
          }}
        >
          ✏️ ערוך
        </button>
      </div>
    </div>
  );
}

export default TaskItem;
