import React, { useEffect, useState } from 'react';
import TaskList from './components/TaskList';
import TaskForm from './components/TaskForm';
import TaskFilter from './components/TaskFilter';
import Modal from './components/Modal';
import { fetchTasks, addTask, updateTask, deleteTask, toggleTask } from './services/taskService';
import './styles/App.css';

function App() {
  const [tasks, setTasks] = useState([]);
  const [filter, setFilter] = useState('all');
  const [isModalOpen, setIsModalOpen] = useState(false);

  useEffect(() => {
    fetchTasks()
      .then(data => setTasks(data))
      .catch(err => console.error(err));
  }, []);

  const handleAddTask = (task) => {
    addTask(task)
      .then(newTask => setTasks(prev => [...prev, newTask]))
      .catch(err => console.error(err));
  };

  const handleUpdateTask = (id, updates) => {
    updateTask(id, updates)
      .then(updatedTask => {
        setTasks(prev =>
          prev.map(task => (task.id === updatedTask.id ? updatedTask : task))
        );
      })
      .catch(err => console.error(err));
  };

  const handleDeleteTask = (id) => {
    deleteTask(id)
      .then(() => setTasks(prev => prev.filter(task => task.id !== id)))
      .catch(err => console.error(err));
  };

  const handleToggleTask = (id) => {
    toggleTask(id)
      .then(updatedTask => {
        setTasks(prev =>
          prev.map(task => (task.id === updatedTask.id ? updatedTask : task))
        );
      })
      .catch(err => console.error(err));
  };

  const filteredTasks = tasks.filter(task => {
    if (filter === 'completed') return task.completed;
    if (filter === 'pending') return !task.completed;
    return true; // all
  });

  return (
    <div className="app">
      <div className="app-container">
        <div className="title-toolbar">
          <h1 className="app-title">Task Manager</h1>
          <div className="toolbar">
            <button 
              className="toolbar-btn" 
              title="Refresh Tasks"
              onClick={() => window.location.reload()}
            >
              â†»
            </button>
            <button 
              className="toolbar-btn" 
              title="Export Tasks"
              onClick={() => {
                const dataStr = JSON.stringify(tasks, null, 2);
                const dataUri = 'data:application/json;charset=utf-8,'+ encodeURIComponent(dataStr);
                const exportFileDefaultName = 'tasks-export.json';
                const linkElement = document.createElement('a');
                linkElement.setAttribute('href', dataUri);
                linkElement.setAttribute('download', exportFileDefaultName);
                linkElement.click();
              }}
            >
              â†“
            </button>
            <button 
              className="toolbar-btn" 
              title="Clear Completed"
              onClick={() => {
                const completedTasks = tasks.filter(task => task.completed);
                completedTasks.forEach(task => handleDeleteTask(task.id));
              }}
            >
              ðŸ—‘
            </button>
            <button 
              className="toolbar-btn" 
              title="Add New Task"
              onClick={() => setIsModalOpen(true)}
            >
              ï¼‹
            </button>
          </div>
        </div>
        <TaskFilter currentFilter={filter} onChange={setFilter} />
        <TaskList
          tasks={filteredTasks}
          allTasks={tasks}
          onUpdate={handleUpdateTask}
          onDelete={handleDeleteTask}
          onToggle={handleToggleTask}
        />
        {/* Floating Add Button */}
        <button 
          className="floating-add-btn"
          onClick={() => setIsModalOpen(true)}
        >
          +
        </button>

        <Modal 
          isOpen={isModalOpen} 
          onClose={() => setIsModalOpen(false)}
          title="Create New Task"
        >
          <TaskForm 
            onAdd={handleAddTask} 
            onClose={() => setIsModalOpen(false)}
          />
        </Modal>
      </div>
    </div>
  );
}

export default App;
