import React, { useEffect, useRef } from 'react';
import TaskItem from './TaskItem';
import '../styles/TaskList.css';

function TaskList({ tasks, onUpdate, onDelete, onToggle }) {
  const carouselRef = useRef(null);

  // אפקט לגלילה אינסופית אוטומטית
  useEffect(() => {
    const carousel = carouselRef.current;
    if (!carousel) return;

    let scrollAmount = 0;
    const scrollStep = 1;
    const scrollInterval = 20;

    const interval = setInterval(() => {
      if (carousel.scrollWidth === 0) return;

      scrollAmount += scrollStep;
      if (scrollAmount >= carousel.scrollWidth / 2) {
        scrollAmount = 0;
      }
      carousel.scrollTo({ left: scrollAmount, behavior: 'smooth' });
    }, scrollInterval);

    return () => clearInterval(interval);
  }, [tasks]);

  if (!tasks || tasks.length === 0) {
    return <p>No tasks yet</p>;
  }

  const duplicatedTasks = [...tasks, ...tasks];

  // פונקציות שליטה ידנית
  const scrollLeft = () => {
    if (carouselRef.current) {
      carouselRef.current.scrollBy({ left: -250, behavior: 'smooth' });
    }
  };

  const scrollRight = () => {
    if (carouselRef.current) {
      carouselRef.current.scrollBy({ left: 250, behavior: 'smooth' });
    }
  };

  return (
    <div className="carousel-container">
      <button className="arrow left" onClick={scrollLeft}>⟵</button>
      <div className="carousel" ref={carouselRef}>
        {duplicatedTasks.map(task => (
          <div className="carousel-item" key={task.id + Math.random()}>
            <TaskItem
              task={task}
              onUpdate={onUpdate}
              onDelete={onDelete}
              onToggle={onToggle}
            />
          </div>
        ))}
      </div>
      <button className="arrow right" onClick={scrollRight}>⟶</button>
    </div>
  );
}

export default TaskList;
