import React, { useEffect, useState } from 'react';
import TaskList from './components/TaskList';
import TaskForm from './components/TaskForm';
import { fetchTasks, addTask, updateTask, deleteTask } from './services/taskService';


function App() {
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    fetchTasks()
      .then(data => setTasks(data))
      .catch(err => console.error(err));
  }, []);

  const handleAddTask = (task) => {
    addTask(task)
      .then(newTask => setTasks(prev => [...prev, newTask]))
      .catch(err => console.error(err));
  };

  const handleUpdateTask = (id, updates) => {
    updateTask(id, updates)
      .then(updatedTask => {
        setTasks(prev =>
          prev.map(task => (task.id === updatedTask.id ? updatedTask : task))
        );
      })
      .catch(err => console.error(err));
  };

  const handleDeleteTask = (id) => {
    deleteTask(id)
      .then(() => setTasks(prev => prev.filter(task => task.id !== id)))
      .catch(err => console.error(err));
  };

  const handleToggleTask = (id) => {
    toggleTask(id)
      .then(updatedTask => {
        setTasks(prev =>
          prev.map(task => (task.id === updatedTask.id ? updatedTask : task))
        );
      })
      .catch(err => console.error(err));
  };

  return (
    <div>
      <h1>Task List</h1>
      <TaskForm onAdd={handleAddTask} />
      <TaskList tasks={tasks} onUpdate={handleUpdateTask} onDelete={handleDeleteTask} />
    </div>
  );
}

export default App;
