import React, { useRef, useEffect } from 'react';
import TaskItem from './TaskItem';
import '../styles/TaskList.css';

function TaskList({ tasks, onUpdate, onDelete, onToggle }) {
  const carouselRef = useRef(null);

  const itemWidth = 270; // רוחב קלף (כולל margin), להתאים ל-CSS
  const duplicatedTasks = [...tasks, ...tasks, ...tasks];

  // כשנטען – נתחיל באמצע
  useEffect(() => {
    if (carouselRef.current) {
      const middle = tasks.length * itemWidth;
      carouselRef.current.scrollLeft = middle;
    }
  }, [tasks]);

  const scrollLeft = () => {
    if (!carouselRef.current) return;
    carouselRef.current.scrollLeft -= itemWidth;

    // אם הגענו קרוב להתחלה – קופצים חזרה לאמצע
    if (carouselRef.current.scrollLeft <= itemWidth) {
      carouselRef.current.scrollLeft += tasks.length * itemWidth;
    }
  };

  const scrollRight = () => {
    if (!carouselRef.current) return;
    carouselRef.current.scrollLeft += itemWidth;

    // אם עברנו את הסוף – קופצים חזרה לאמצע
    if (carouselRef.current.scrollLeft >= (tasks.length * 2 * itemWidth)) {
      carouselRef.current.scrollLeft -= tasks.length * itemWidth;
    }
  };

  if (!tasks || tasks.length === 0) {
    return <p>No tasks yet</p>;
  }

  return (
    <div className="carousel-wrapper">
      <button className="arrow left" onClick={scrollLeft}>⟵</button>
      <div className="carousel-container" ref={carouselRef}>
        {duplicatedTasks.map((task, index) => (
          <div className="carousel-item" key={task.id + '-' + index}>
            <TaskItem
              task={task}
              onUpdate={onUpdate}
              onDelete={onDelete}
              onToggle={onToggle}
            />
          </div>
        ))}
      </div>
      <button className="arrow right" onClick={scrollRight}>⟶</button>
    </div>
  );
}

export default TaskList;
