import React, { useEffect, useRef } from 'react';
import TaskItem from './TaskItem';
import '../styles/TaskList.css';

function TaskList({ tasks, onUpdate, onDelete, onToggle }) {
  const carouselRef = useRef(null);
  const intervalRef = useRef(null);

  useEffect(() => {
    const carousel = carouselRef.current;
    if (!carousel) return;

    const scrollStep = 1;

    intervalRef.current = setInterval(() => {
      carousel.scrollLeft += scrollStep;
      if (carousel.scrollLeft >= carousel.scrollWidth / 2) {
        carousel.scrollLeft = 0;
      }
    }, 20);

    return () => clearInterval(intervalRef.current);
  }, [tasks]);

  if (!tasks || tasks.length === 0) {
    return <p>No tasks yet</p>;
  }

  const duplicatedTasks = [...tasks, ...tasks];

  // שליטה ידנית
  const scrollLeft = () => {
    if (carouselRef.current) {
      carouselRef.current.scrollLeft -= 250;
      if (carouselRef.current.scrollLeft < 0) {
        carouselRef.current.scrollLeft = carouselRef.current.scrollWidth / 2;
      }
    }
  };

  const scrollRight = () => {
    if (carouselRef.current) {
      carouselRef.current.scrollLeft += 250;
      if (carouselRef.current.scrollLeft >= carouselRef.current.scrollWidth / 2) {
        carouselRef.current.scrollLeft = 0;
      }
    }
  };

  return (
    <div className="carousel-wrapper">
      <button className="arrow left" onClick={scrollLeft}>⟵</button>
      <div className="carousel-container" ref={carouselRef}>
        {duplicatedTasks.map((task, index) => (
          <div className="carousel-item" key={task.id + '-' + index}>
            <TaskItem
              task={task}
              onUpdate={onUpdate}
              onDelete={onDelete}
              onToggle={onToggle}
            />
          </div>
        ))}
      </div>
      <button className="arrow right" onClick={scrollRight}>⟶</button>
    </div>
  );
}

export default TaskList;
