import React from 'react';

function TaskFilter({ currentFilter, onChange }) {
  return (
    <div style={{ marginBottom: '1rem' }}>
      <button
        onClick={() => onChange('all')}
        style={{ fontWeight: currentFilter === 'all' ? 'bold' : 'normal' }}
      >
        All
      </button>
      <button
        onClick={() => onChange('completed')}
        style={{ fontWeight: currentFilter === 'completed' ? 'bold' : 'normal', marginLeft: '0.5rem' }}
      >
        Completed
      </button>
      <button
        onClick={() => onChange('pending')}
        style={{ fontWeight: currentFilter === 'pending' ? 'bold' : 'normal', marginLeft: '0.5rem' }}
      >
        Pending
      </button>
    </div>
  );
}

export default TaskFilter;
