import React, { useEffect, useRef } from 'react';
import TaskItem from './TaskItem';
import '../styles/TaskList.css';

function TaskList({ tasks, onUpdate, onDelete, onToggle }) {
  const carouselRef = useRef(null);

  useEffect(() => {
    const carousel = carouselRef.current;
    if (!carousel) return;

    const scrollStep = 1; // פיקסלים בכל צעד
    const interval = setInterval(() => {
      if (!carousel) return;

      carousel.scrollLeft += scrollStep;

      // אם עברנו חצי (כלומר סוף הרשימה הראשונה) → קופצים להתחלה
      if (carousel.scrollLeft >= carousel.scrollWidth / 2) {
        carousel.scrollLeft = 0;
      }
    }, 20); // כל 20ms

    return () => clearInterval(interval);
  }, [tasks]);

  if (!tasks || tasks.length === 0) {
    return <p>No tasks yet</p>;
  }

  const duplicatedTasks = [...tasks, ...tasks];

  return (
    <div className="carousel-container" ref={carouselRef}>
      {duplicatedTasks.map((task, index) => (
        <div className="carousel-item" key={task.id + '-' + index}>
          <TaskItem
            task={task}
            onUpdate={onUpdate}
            onDelete={onDelete}
            onToggle={onToggle}
          />
        </div>
      ))}
    </div>
  );
}

export default TaskList;
